import { Component } from '@angular/core';

@Component({
  selector: 'app-faq',
  templateUrl: './faq.component.html',
  styleUrls: ['./faq.component.css']
})
export class FaqComponent {

  pressed: boolean = false;

  questions = [
    {
      text: 'QUESTION 1: What is the minimum age to join?',
      answer:
        'Students will be accepted starting at 2 years old (boys and girls). Students will be placed and relocated by the instructor into their respective groups according to their age and knowledge of the subject.',
      pressed: false
    },
    {
      text: 'QUESTION 2: What are the dress code requirements?',
      answer: 'Students are required to wear proper dance attire as per guidelines.',
      pressed: false
    }
  ];

  buttonPressed(): void{
    this.pressed = !this.pressed;
  }
}
<div class = "container">
    <div class = "title">
        FREQUENTLY ASKED QUESTIONS
    </div>
    <div class = "fq" *ngFor = "let question of questions; let i = index" >
        <div class="agreement">Discipline is very important in Dance. By being part of REYNODANCE ACADEMY, I commit to following its guidelines.</div>

        <div class = "box">
            <div class = "question">
                {{question.text}}
            </div>
            <button *ngIf = "pressed==false" class = "show" (click) = "buttonPressed()">></button>
            <button *ngIf = "pressed" class = "hide" (click) = "buttonPressed()">></button>
        </div>
        <div class = "answer" *ngIf="pressed == true">
            {{ question.answer}}
        </div>
    </div>

</div>
