import { Component } from '@angular/core';

@Component({
  selector: 'app-schedule',
  templateUrl: './schedule.component.html',
  styleUrls: ['./schedule.component.css']
})
export class ScheduleComponent {
  selector = [
    {
      group: 'Little Ballet',
      classes:'[tap, ballet]',
    },
    {
      group: 'Ballet I',
      classes:'[tap, jazz, aerial, ballet]',
    }
  ]
}
