import { Component } from '@angular/core';

@Component({
  selector: 'app-schedule',
  templateUrl: './schedule.component.html',
  styleUrls: ['./schedule.component.css']
})
export class ScheduleComponent {
  // Groups
  groups = ['PRE-BALLET', 'LITTLE BALLET', 'BALLET I', 'BALLET II', 'ELITE TEEN'];

  // Group Summaries
  groupSummaries = {
    'PRE-BALLET': 'Students will be accepted starting at 2 years old (boys and girls)...',
    'LITTLE BALLET': 'Students will be placed and relocated by the instructor...',
    'BALLET I': 'This group includes a mix of tap, jazz, aerial, and ballet...',
    'BALLET II': 'Students advance their skills with more rigorous techniques...',
    'ELITE TEEN': 'A specialized group for advanced-level teens...'
  };

  // Weekly Schedules
  week = [
    {
      name: 'Monday',
      schedules: [
        { group: 'PRE-BALLET', time: '5-6PM' },
        { group: 'BALLET I', time: '6-7PM' }
      ]
    },
    {
      name: 'Tuesday',
      schedules: [
        { group: 'LITTLE BALLET', time: '4-5PM' },
        { group: 'BALLET II', time: '5-6PM' }
      ]
    },
    {
      name: 'Wednesday',
      schedules: [
        { group: 'ELITE TEEN', time: '7-8PM' },
        { group: 'PRE-BALLET', time: '5-6PM' }
      ]
    }
    // Add more days as needed
  ];

  // Selected Groups
  selectedGroups: string[] = [];

  // Toggle Group Selection
  toggleGroup(group: string): void {
    const index = this.selectedGroups.indexOf(group);
    if (index === -1) {
      this.selectedGroups.push(group);
    } else {
      this.selectedGroups.splice(index, 1);
    }
  }
}
