import { Component } from '@angular/core';

@Component({
  selector: 'app-sign-in',
  templateUrl: './sign-in.component.html',
  styleUrls: ['./sign-in.component.css']
})
export class SignInComponent {

  signIn(): void{

  }
}
<div class="container">
  <div class="register">
    <a href="/register">Register Now!</a>
  </div>
  <div class = "background">
    <img class = "logo" src = "/assets/images/logo.png"/>
    <div class="reynodance">ReynoDance Academy Sign-In</div>
    <div class = "title"> Sign-In to view your dashboard!</div>
    <form class = "sign-in" (ngSubmit)="signIn()">
      Username:<input name = "username" type="text" email/>
      Password:<input name = "username" type="text" />
    </form>
    <a href= "/dashboard" ><button class = "login" type="submit">Sign-In</button></a>
  </div>
</div>
